import React from 'react'
import axios from 'axios'
import Header from '../components/header'
import Search from '../components/search'
import Item from '../components/item'
class Home extends React.Component {

  constructor(props){
    super(props)    
      this.state={
        courses: null
      }

    this.getCourses = this.getCourses.bind(this)
    this.getFromFirebase = this.getFromFirebase.bind(this)
    // this.alertTest = this.alertTest.bind(this)

}   


  componentDidMount() {
    this.getFromFirebase()
  }

  getFromFirebase(){
    var self = this
    let app = this.props.db.database().ref('courses');
    app.once('value').then(function(snapshot) {
        var c = snapshot.val()
        // console.log(snapshot.val())  
        self.getCourses(c)
      });

  }

  getCourses(value){
    this.setState({
      courses: value
    })
    console.log(this.state.courses)
  }

  alertTest(){
    alert(this.state.courses)
  }

  async getAPI(){
  //   axios.get('https://ywchw-221815.firebaseio.com/courses.json')
  //   .then((response) => {
  //     console.log(response);
  //     this.setState({courses: response.data})
  //     console.log(this.state.courses)
  //   })
  //  .catch((error)=>{
  //     console.log(error);
  //  });
  }







  render() {
    return (
      <div>
        <Header />
        <Search />

        {
          this.state.courses.map(item => {
            return (
              <p>{item.key()}</p>
              // <Item />
            )
          })

        }
        <button onClick={() => {alert(this.state.courses)}}></button>
        <Item nameCourse={this.state.courses} />
        <Item />
        <Item />
        <Item />
        <Item />
      </div>
    )
  }
}

export default Home
