import React,{Component} from 'react'
import SearchBoxImage from '../assets/img/search_box.png'
import SearchIconImage from '../assets/img/search.png'
import style from 'styled-components'
class Search extends Component{
    constructor(props){
        super(props)
        this.state = {
            value: ""
        }
        this.handleChange = this.handleChange.bind(this)
        this.sendDataBack = this.sendDataBack.bind(this)
    }
    // componentDidUpdate(prevProps, prevState) {
    //     if (this.state.value !== prevState.value) {

    //     }
    //   }
    sendDataBack(){
        this.props.itemSearch(this.state.value);  
    }

    handleChange(e){

        console.log(e.target.value)
        this.setState({
            value: e.target.value
        })



    }




    render(){
    const SearchBox = style.input`
    font-family: 'Mali', cursive;
    background-image: url(${SearchBoxImage});
    background-repeat: no-repeat;
    background-position: center;
    background-size: cover;
    width: 55%;
    height: 55px;
    font-size: 15px;
    border: none;
    border-radius: 16px;
    outline: none;
    padding-left: 30px;
    padding-top: -90px;
    top: 0px;
    `;




    return(
            <div>

               <SearchBox 
                    autoFocus
                    placeholder="ค้นหาหัวข้อที่ต้องการดูชั่วโมงเฉลี่ยในการเรียนรู้ ..."
                    type="text"
                    value={this.state.value} 
                    onChange={this.handleChange} />
            </div>
        );
    } 

}

export default Search;
