import React,{Component} from 'react'
import style from 'styled-components'
import modal from 'react-modal'
import TimeField from 'react-simple-timefield';

class Modal extends Component{
    constructor(props){
        super(props)
        this.state ={
            showModal: false,
            time: "01:00"
        }
    this.showModal = this.showModal.bind(this)
    this.hideModal = this.hideModal.bind(this)
    this.onTimeChangeHandler = this.onTimeChangeHandler.bind(this)
    }

  componentWillReceiveProps(nextProps){
    this.setState({
      showModal: nextProps.showModal
    })
  }

  showModal = () => {
    this.setState({ showModal: true });
  };

  hideModal = () => {
    this.setState({ showModal: false });
  };

  onTimeChangeHandler(time){
    this.setState({time},()=>{console.log(this.state.time)})
  }

    render(){
        const Modal = style(modal)`
        text-align: center;
        font-family: 'Mali';
        margin-top: 8%;
        margin-left: 25%;
        margin-right: 25%;
        height: 50%;
        background-color: #FFF;
        box-shadow: 0 0 13px rgba(0, 0, 0, .05), 0 0px 32px rgba(0, 0, 0, .08);
        border-radius: 36px;
        outline: none;
        `


    const {time} =this.state
    return(

        <div>
          <Modal isOpen={this.state.showModal} onRequestClose={this.hideModal} >

            <p style={{textAlign: 'right', paddingRight:'20px', paddingTop:'10px', fontSize: '30px', cursor:'default'}} onClick={this.hideModal} >x</p>
            <p style={{fontSize: '20px'}}>ใส่เวลาในการเรียนรู้ของหัวข้อนี้</p>

            <TimeField value={time} onChange={this.onTimeChangeHandler}
            style={{
              border: '2px solid #666',
              fontSize: 42,
              width: 107,
              padding: '5px 8px',
              color: '#333',
              borderRadius: 3,
              fontFamily: 'Mali',
              outline: 'none',
              paddingRight: '5px'
            }} />

            <p>HH:MM</p>






          </Modal>

        </div>
    );
    } 

}

export default Modal;
