import React,{Component} from 'react'
import SearchBoxImage from '../assets/img/search_box.png'
import SearchIconImage from '../assets/img/search.png'
import style from 'styled-components'
class Search extends Component{
    constructor(props){
        super(props)
        this.state = {
            value: null
        }
        this.onChange = this.onChange.bind(this)
    }

    onChange({ target: { value } }) {
        const { index, lines } = this.state;

        // Set captured value to input
        this.setState(() => ({ value }));
        }

    render(){
    const SearchBox = style.input`
    font-family: 'Mali', cursive;
    background-image: url(${SearchBoxImage});
    background-repeat: no-repeat;
    background-position: center;
    background-size: cover;
    width: 55%;
    height: 55px;
    font-size: 15px;
    border: none;
    border-radius: 16px;
    outline: none;
    padding-left: 30px;
    padding-top: -90px;
    top: 0px;
    `;



    const {value} = this.state 
    return(
            <div>
               <SearchBox 
                    placeholder="ค้นหาหัวข้อที่ต้องการดูชั่วโมงเฉลี่ยในการเรียนรู้ ..."
                    type="text"
                    value={value}
                    onChange={e => this.onChange(this.value)}
               />
            </div>
        );
    } 

}

export default Search;
